export const commonBuiltInParserOptionsSchema = z
  .object({
    shouldExecuteRemotely: z.boolean().optional(),
  })
  .strict();
export type CommonBuiltInParserOptionsSchema = typeof commonBuiltInParserOptionsSchema;
export type CommonBuiltInParserOptions = z.infer<typeof commonBuiltInParserOptionsSchema>;

export const optionalCommonBuiltInParserOptionsSchema = commonBuiltInParserOptionsSchema.optional();
