import { z } from 'zod';
import { createBuiltInParserDefinition } from '../../internals/createBuiltInParserDefinition.js';

export const svgToJsxParserName = 'svg-to-jsx';

export const SvgToJsxParserOptionsSchema = z
  .object({
    tsx: z.boolean().optional(),
    filePrefix: z.string().optional(),
    fileSuffix: z.string().optional(),
    reactVersion: z.string().optional(),
    exportDefault: z.boolean().optional(),
    tokenNameTemplate: z.string().optional(),
  })
  .strict();

export const svgToJsxParserDefinition = createBuiltInParserDefinition({
  name: svgToJsxParserName,
  kind: 'generation',
  hasOptionalOptions: true,
  optionsSchema: SvgToJsxParserOptionsSchema,
  hasOptionalOutput: false,
  outputTypes: ['directory'],
  inTypes: ['SDTF', 'SDTF Engine', 'vector'],
  outType: 'vector',
});

export type SvgToJsxParserDefinition = typeof svgToJsxParserDefinition;
export type SvgToJsxParserOptions = z.infer<SvgToJsxParserDefinition['optionsSchema']>;
