import { SpecifyTextStyleValue } from '@specifyapp/specify-design-token-format';

import { NonNullableOptions } from '../NonNullableOptions.js';
import { fontValueToCssFontImport } from './font.js';

export const textStyleValueToCssFontImport = (
  value: SpecifyTextStyleValue,
  { formats, fontsPath, includeFontWeight, genericFamily, fontDisplay }: NonNullableOptions,
) => {
  return fontValueToCssFontImport(value.font, {
    formats,
    fontsPath,
    includeFontWeight,
    genericFamily,
    fontDisplay,
  });
};
