export const convertToCssClasses = (
  outputs: Array<{ variableName: string; value: Record<string, unknown> }>,
): string => {
  return outputs
    .map(output => {
      return `.${output.variableName} {
${Object.entries(output.value)
  .map(([key, value]) => `\t${key}: ${value};`)
  .join('\n')}
}`;
    })
    .join('\n');
};
