import { z } from 'zod';
import { createBuiltInParserDefinition } from '../../internals/createBuiltInParserDefinition.js';

export const toFlutterParserName = 'to-flutter';

export const toFlutterParserOptionsSchema = z
  .object({
    tokenNameTemplate: z.string().optional(),
  })
  .strict();

export const toFlutterParserDefinition = createBuiltInParserDefinition({
  name: toFlutterParserName,
  kind: 'generation',
  hasOptionalOptions: true,
  optionsSchema: toFlutterParserOptionsSchema,
  hasOptionalOutput: false,
  outputTypes: ['file'],
  inTypes: ['SDTF', 'SDTF Engine'],
  outType: 'SDTF Engine',
});
export type ToFlutterParserDefinition = typeof toFlutterParserDefinition;
export type ToFlutterParserOptions = z.infer<ToFlutterParserDefinition['optionsSchema']>;
