import { SpecifyDurationValue } from '@specifyapp/specify-design-token-format';

export function renderDuration(duration: SpecifyDurationValue) {
  // Values are int, so we force number to be an int
  return `Duration(${duration.unit === 's' ? 'seconds:' : 'milliseconds:'} ${duration.value.toFixed(
    0,
  )})`;
}
