import { SpecifyFontValue } from '@specifyapp/specify-design-token-format';
import { renderFontWeight } from './fontWeight.js';

export function renderFont(font: SpecifyFontValue) {
  return `TextStyle(fontFamily: "${font.postScriptName}", fontStyle: ${
    font.style === 'italic' ? 'FontStyle.italic' : 'FontStyle.normal'
  }, fontWeight: ${renderFontWeight(font.weight)})`;
}
