import { SpecifyGradientsValue } from '@specifyapp/specify-design-token-format';
import { renderGradient } from './gradient.js';
import { MultipleConversionOutput } from '../convertTokens.js';

export function renderGradients(
  gradients: SpecifyGradientsValue,
  variableName: string,
): MultipleConversionOutput {
  return gradients.flatMap<MultipleConversionOutput[number]>((v, i) => {
    const gradient = renderGradient(v);

    if (!gradient) return [];

    return [
      {
        value: gradient,
        variableName: `${variableName}${i}`,
        type: 'gradient',
      },
    ];
  });
}
