import { SpecifyRadiiValue } from '@specifyapp/specify-design-token-format';
import { renderRadius } from './radius.js';

export function renderRadii(radii: SpecifyRadiiValue) {
  const radiuses = radii.map(renderRadius);

  if (radiuses.length === 1) {
    return `BorderRadius.all(${radiuses[0]})`;
  } else if (radiuses.length === 2) {
    return `BorderRadius.horizontal(left: ${radiuses[0]}, right: ${radiuses[1]})`;
  } else if (radiuses.length === 3) {
    return `BorderRadius.only(topLeft: ${radiuses[0]}, topRight: ${radiuses[1]}, bottomLeft: ${radiuses[1]}, bottomRight: ${radiuses[2]})`;
  } else if (radiuses.length === 4) {
    return `BorderRadius.only(topLeft: ${radiuses[0]}, topRight: ${radiuses[1]}, bottomLeft: ${radiuses[2]}, bottomRight: ${radiuses[3]})`;
  } else {
    return 'BorderRadius.all(0.0)';
  }
}
