import { SpecifyShadowValue } from '@specifyapp/specify-design-token-format';
import { renderColor } from './color.js';
import { numberToDouble } from '../utils.js';

export function renderShadow(shadow: SpecifyShadowValue) {
  const color = renderColor(shadow.color);

  if (!color) return undefined;

  return `BoxShadow(color: ${color}, offset: Offset(${numberToDouble(
    shadow.offsetX.value,
  )}, ${numberToDouble(shadow.offsetY.value)}), blurRadius: ${numberToDouble(
    shadow.blurRadius.value,
  )}, spreadRadius: ${numberToDouble(shadow.spreadRadius.value)})`;
}
