import { SpecifyShadowsValue } from '@specifyapp/specify-design-token-format';
import { renderShadow } from './shadow.js';
import { MultipleConversionOutput } from '../convertTokens.js';

export function renderShadows(shadows: SpecifyShadowsValue, variableName: string) {
  return shadows.flatMap<MultipleConversionOutput[number]>((v, i) => {
    const shadow = renderShadow(v);

    if (!shadow) return [];

    return [
      {
        value: shadow,
        variableName: `${variableName}${i}`,
        type: 'shadow',
      },
    ];
  });
}
