import { SpecifySpacingsValue } from '@specifyapp/specify-design-token-format';
import { numberToDouble } from '../utils.js';

export function renderSpacings(value: SpecifySpacingsValue) {
  const spacings = value.map(v => numberToDouble(v.value));

  if (spacings.length === 1) {
    return `EdgeInsets.all(${spacings[0]})`;
  } else if (spacings.length === 2) {
    return `EdgeInsets.fromLTRB(${spacings[1]}, ${spacings[0]}, ${spacings[0]}, ${spacings[1]})`;
  } else if (spacings.length === 3) {
    return `EdgeInsets.fromLTRB(${spacings[1]}, ${spacings[0]}, ${spacings[2]}, ${spacings[1]})`;
  } else if (spacings.length === 4) {
    return `EdgeInsets.fromLTRB(${spacings[3]}, ${spacings[0]}, ${spacings[2]}, ${spacings[1]})`;
  } else {
    return 'EdgeInsets.all(0.0)';
  }
}
