import { SpecifyTextStyleValue } from '@specifyapp/specify-design-token-format';

export function renderTextAlignHorizontal(textAlign: SpecifyTextStyleValue['textAlignHorizontal']) {
  switch (textAlign) {
    case 'end':
    case 'right':
      return 'TextAlign.right';
    case 'initial':
    case 'left':
    case 'start':
      return 'TextAlign.left';
    case 'center':
      return 'TextAlign.center';
    case 'justify':
    case 'justify-all':
      return 'TextAlign.justify';
    case 'match-parent':
      return undefined;
  }
}
