import { SpecifyTextDecorationValue } from '@specifyapp/specify-design-token-format';

export function renderTextDecoration(textDecoration: SpecifyTextDecorationValue) {
  switch (textDecoration) {
    case 'none':
    case 'wavy':
    case 'dashed':
      return 'TextDecoration.none';
    case 'overline':
      return 'TextDecoration.overline';
    case 'underline':
      return 'TextDecoration.underline';
    case 'line-through':
      return 'TextDecoration.lineThrough';
  }
}
