import { z } from 'zod';
import { toJavascriptOptionsSchema } from '../../shared/definition.js';
import { createBuiltInParserDefinition } from '../../internals/createBuiltInParserDefinition.js';

export const toJavascriptParserName = 'to-javascript';

export const toJavascriptParserDefinition = createBuiltInParserDefinition({
  name: toJavascriptParserName,
  kind: 'generation',
  hasOptionalOptions: true,
  optionsSchema: toJavascriptOptionsSchema,
  hasOptionalOutput: false,
  outputTypes: ['file'],
  inTypes: ['SDTF', 'SDTF Engine'],
  outType: 'SDTF Engine',
});

export type ToJavascriptParserDefinition = typeof toJavascriptParserDefinition;
export type ToJavascriptParserOptions = z.infer<ToJavascriptParserDefinition['optionsSchema']>;
