import { SpecifyGradientValue } from '@specifyapp/specify-design-token-format';
import { renderColor } from './color.js';

export function renderGradient(gradient: SpecifyGradientValue) {
  const colors = gradient.colorStops
    .map(({ color, position }) => `${renderColor(color)} ${position * 100}%`)
    .join(', ');

  if (colors.length === 0) return undefined;

  switch (gradient.type) {
    case 'conic': {
      return `conic-gradient(from ${gradient.angle}deg at ${gradient.position}, ${colors})`;
    }
    case 'radial': {
      return `radial-gradient(circle at ${gradient.position}, ${colors})`;
    }
    case 'linear': {
      return `linear-gradient(${gradient.angle}deg, ${colors})`;
    }
  }
}
