import { SpecifyTransitionValue } from '@specifyapp/specify-design-token-format';
import { renderDuration } from './duration.js';

export function renderTransition(transition: SpecifyTransitionValue) {
  return {
    delay: renderDuration(transition.delay),
    duration: renderDuration(transition.duration),
    timingFunction: Array.isArray(transition.timingFunction)
      ? `cubic-bezier(${transition.timingFunction.join(', ')})`
      : `steps(${transition.timingFunction.stepsCount}, ${transition.timingFunction.jumpTerm})`,
  };
}
