import { SpecifyFontValue } from '@specifyapp/specify-design-token-format';
import { jsonToken } from '../tokenToJson.js';

export function fontToJson(value: SpecifyFontValue): Array<jsonToken> {
  return [
    { value: value.postScriptName, nameExtension: 'fontPostScriptName' },
    {
      value: value.weight,
      nameExtension: 'fontWeight',
    },
    {
      value: value.family,
      nameExtension: 'fontFamily',
    },
    {
      value: value.style,
      nameExtension: 'fontStyle',
    },
    {
      value: value.files,
      nameExtension: 'files',
    },
  ];
}
