import { SpecifyGradientsValue } from '@specifyapp/specify-design-token-format';
import { gradientToJson } from './gradient.js';

export function gradientsToJson(value: SpecifyGradientsValue) {
  return value
    .flatMap(v => {
      const converted = gradientToJson(v);
      return !!converted ? [converted] : [];
    })
    .join(', ');
}
