import { SpecifyShadowValue } from '@specifyapp/specify-design-token-format';
import { colorToJson } from './color.js';
import { dimensionToJson } from './dimension.js';

export function shadowToJson(value: SpecifyShadowValue) {
  return `${value.type === 'inner' ? 'inset ' : ''}${dimensionToJson(
    value.offsetX,
  )} ${dimensionToJson(value.offsetY)} ${dimensionToJson(value.blurRadius)} ${
    colorToJson(value.color) ?? ''
  }`;
}
