import { z } from 'zod';
import { createBuiltInParserDefinition } from '../../internals/createBuiltInParserDefinition.js';

export const toJsonParserName = 'to-json';

export const toJsonParserDefinition = createBuiltInParserDefinition({
  name: toJsonParserName,
  kind: 'generation',
  hasOptionalOptions: true,
  optionsSchema: z
    .object({
      output: z.enum(['raw', 'css']).optional(),
    })
    .strict(),
  hasOptionalOutput: false,
  outputTypes: ['file'],
  inTypes: ['SDTF', 'SDTF Engine'],
  outType: 'SDTF Engine',
});

export type ToJsonParserDefinition = typeof toJsonParserDefinition;
export type ToJsonParserOptions = z.infer<ToJsonParserDefinition['optionsSchema']>;
