import { z } from 'zod';
import { createBuiltInParserDefinition } from '../../internals/createBuiltInParserDefinition.js';

export const toKotlinParserName = 'to-kotlin';

export const toKotlinParserDefinition = createBuiltInParserDefinition({
  name: toKotlinParserName,
  kind: 'generation',
  hasOptionalOptions: true,
  optionsSchema: z
    .object({
      tokenNameTemplate: z.string().optional(),
      scopeName: z.string().optional(),
      androidMinVersion: z.number().optional(),
    })
    .strict(),
  hasOptionalOutput: false,
  outputTypes: ['file'],
  inTypes: ['SDTF', 'SDTF Engine'],
  outType: 'SDTF Engine',
});

export type ToKotlinParserDefinition = typeof toKotlinParserDefinition;
export type ToKotlinParserOptions = z.infer<ToKotlinParserDefinition['optionsSchema']>;
