import { SpecifyDimensionValue } from '@specifyapp/specify-design-token-format';

export function dimensionToKotlin(dimension: SpecifyDimensionValue) {
  if (dimension.unit === 'px') {
    return `pxToDp(${dimension.value.toString()})`;
  } else {
    return `/* ${dimension.unit} */ ${dimension.value.toString()}`;
  }
}
