import { SpecifyFontValue } from '@specifyapp/specify-design-token-format';
import { fontWeightToKotlin } from './fontWeight.js';

export function fontToKotlin(font: SpecifyFontValue) {
  return `Font(resId = R.font.${font.postScriptName}, weight = ${fontWeightToKotlin(
    font.weight,
  )}, style = ${font.style === 'normal' ? 'FontStyle.Normal' : 'FontStyle.Italic'})`;
}
