import { SpecifyTextStyleValue } from '@specifyapp/specify-design-token-format';

export function textAlignmentHorizontalToKotlin(
  textAlign: SpecifyTextStyleValue['textAlignHorizontal'],
) {
  switch (textAlign) {
    case 'end':
      return 'TextAlign.End';
    case 'right':
      return 'TextAlign.Right';
    case 'initial':
    case 'left':
      return 'TextAlign.Left';
    case 'start':
      return 'TextAlign.Start';
    case 'center':
      return 'TextAlign.Center';
    case 'justify':
    case 'justify-all':
      return 'TextAlign.Justify';
    case 'match-parent':
      return undefined;
  }
}
