import { z } from 'zod';
import { toJavascriptOptionsSchema } from '../../shared/definition.js';
import { createBuiltInParserDefinition } from '../../internals/createBuiltInParserDefinition.js';

export const toReactNativeParserName = 'to-react-native';

export const toReactNativeParserDefinition = createBuiltInParserDefinition({
  name: toReactNativeParserName,
  kind: 'generation',
  hasOptionalOptions: true,
  optionsSchema: toJavascriptOptionsSchema,
  hasOptionalOutput: false,
  outputTypes: ['file'],
  inTypes: ['SDTF', 'SDTF Engine'],
  outType: 'SDTF Engine',
});

export type ToReactNativeParserDefinition = typeof toReactNativeParserDefinition;
export type ToReactNativeParserOptions = z.infer<ToReactNativeParserDefinition['optionsSchema']>;
