import { SpecifyBorderValue } from '@specifyapp/specify-design-token-format';
import { renderColor } from './color.js';
import { renderDimension } from './dimension.js';
import { renderRadii } from './radii.js';

export function renderBorder(border: SpecifyBorderValue) {
  const variables = {
    borderWidth: renderDimension(border.width),
    color: renderColor(border.color),
  } as { borderWidth: string | number; color: string; borderRadius?: Array<string | number> };

  if (!!border.rectangleCornerRadii)
    variables.borderRadius = renderRadii(border.rectangleCornerRadii);

  return variables;
}
