import { SpecifyShadowValue } from '@specifyapp/specify-design-token-format';
import { renderColor } from './color.js';
import { renderDimension } from './dimension.js';

export function renderShadow(shadow: SpecifyShadowValue) {
  const variables = {
    type: shadow.type,
    offsetX: renderDimension(shadow.offsetX),
    offsetY: renderDimension(shadow.offsetY),
    color: renderColor(shadow.color),
  } as {
    type: string;
    offsetX: string | number;
    offsetY: string | number;
    color: string;
    blurRadius?: string | number;
    spreadRadius?: string | number;
  };

  if (!!shadow.blurRadius) variables.blurRadius = renderDimension(shadow.blurRadius);

  if (!!shadow.spreadRadius) variables.spreadRadius = renderDimension(shadow.spreadRadius);

  return variables;
}
