import { z } from 'zod';
import { createBuiltInParserDefinition } from '../../internals/createBuiltInParserDefinition.js';

export const toScssMapParserName = 'to-scss-map';

export const toScssMapParserDefinition = createBuiltInParserDefinition({
  name: toScssMapParserName,
  kind: 'generation',
  hasOptionalOptions: true,
  optionsSchema: z
    .object({
      tokenNameTemplate: z.string().optional(),
    })
    .strict(),
  hasOptionalOutput: false,
  outputTypes: ['directory'],
  inTypes: ['SDTF', 'SDTF Engine'],
  outType: 'SDTF Engine',
});

export type ToScssMapParserDefinition = typeof toScssMapParserDefinition;
export type ToScssMapParserOptions = z.infer<ToScssMapParserDefinition['optionsSchema']>;
