import { z } from 'zod';
import { createBuiltInParserDefinition } from '../../internals/createBuiltInParserDefinition.js';

export const toScssVariablesParserName = 'to-scss-variables';

const optionsSchema = z
  .object({
    tokenNameTemplate: z.string().optional(),
    shouldRenderSingleMode: z.boolean().optional(),
  })
  .strict();

export const toScssVariablesParserDefinition = createBuiltInParserDefinition({
  name: toScssVariablesParserName,
  kind: 'generation',
  hasOptionalOptions: true,
  optionsSchema,
  hasOptionalOutput: false,
  outputTypes: ['file'],
  inTypes: ['SDTF', 'SDTF Engine'],
  outType: 'SDTF Engine',
});

export type ToScssVariablesParserDefinition = typeof toScssVariablesParserDefinition;
export type ToScssVariablesParserOptions = z.infer<typeof optionsSchema>;
