import { RawValueSignature } from '@specifyapp/specify-design-token-format';

export const bitmapToStyleDictionary = (bitmap: RawValueSignature<'bitmap'>) => {
  const variationLabel = bitmap.variationLabel.resolveDeepValue().unwrapValue();

  return {
    url: bitmap.url.resolveDeepValue().unwrapValue(),
    ...(variationLabel ? { variationLabel } : {}),
    width: bitmap.width.resolveDeepValue().unwrapValue(),
    height: bitmap.height.resolveDeepValue().unwrapValue(),
    format: bitmap.format.resolveDeepValue().unwrapValue(),
  };
};
