import { RawValueSignature } from '@specifyapp/specify-design-token-format';

export const sizeToStyleDictionary = (value: RawValueSignature<'dimension'>) =>
  `${value.value.resolveDeepValue().unwrapValue()}${value.unit
    .resolveDeepValue()
    .mapPrimitiveValue(v => (v === null ? '' : v))
    .unwrapValue()}`;
