import { RawValueSignature } from '@specifyapp/specify-design-token-format';
import { sizeToStyleDictionary } from './size.js';

export const spacingsToStyleDictionary = (spacings: RawValueSignature<'spacings'>) =>
  spacings.map(spacing =>
    spacing.resolveDeepValue().mapPrimitiveValue(sizeToStyleDictionary).unwrapValue(),
  );
