import { RawValueSignature } from '@specifyapp/specify-design-token-format';

export const vectorToStyleDictionary = (vector: RawValueSignature<'vector'>) => {
  const variationLabel = vector.variationLabel.resolveDeepValue().unwrapValue();

  return {
    url: vector.url.resolveDeepValue().unwrapValue(),
    format: vector.format.resolveDeepValue().unwrapValue(),
    ...(variationLabel ? { variationLabel } : {}),
  };
};
