import { z } from 'zod';
import { createBuiltInParserDefinition } from '../../internals/createBuiltInParserDefinition.js';

export const toStyleDictionaryParserName = 'to-style-dictionary';

export const toStyleDictionaryParserDefinition = createBuiltInParserDefinition({
  name: toStyleDictionaryParserName,
  kind: 'generation',
  hasOptionalOptions: true,
  optionsSchema: z.object({}).strict(),
  hasOptionalOutput: false,
  outputTypes: ['directory'],
  inTypes: ['SDTF', 'SDTF Engine'],
  outType: 'SDTF Engine',
});

export type ToStyleDictionaryParserDefinition = typeof toStyleDictionaryParserDefinition;
export type ToStyleDictionaryParserOptions = z.infer<
  ToStyleDictionaryParserDefinition['optionsSchema']
>;
