import { z } from 'zod';
import { createBuiltInParserDefinition } from '../../internals/createBuiltInParserDefinition.js';

export const toSvgFileParserName = 'to-svg-file';

export const toSvgFileParserDefinition = createBuiltInParserDefinition({
  name: toSvgFileParserName,
  kind: 'generation',
  hasOptionalOptions: true,
  optionsSchema: z
    .object({
      filenameTemplate: z.string().optional(),
    })
    .strict(),
  hasOptionalOutput: false,
  outputTypes: ['directory'],
  inTypes: ['SDTF', 'SDTF Engine', 'vector'],
  outType: 'vector',
});

export type ToSvgFileParserDefinition = typeof toSvgFileParserDefinition;
export type ToSvgFileParserOptions = z.infer<ToSvgFileParserDefinition['optionsSchema']>;
