import { SpecifyFontValue } from '@specifyapp/specify-design-token-format';
import { fontWeightToSwift } from './fontWeight.js';
import { MultipleConversionOutput } from '../convertTokens.js';

export function fontToSwift(font: SpecifyFontValue, variableName: string) {
  return [
    { variableName, value: `"${font.postScriptName}"`, type: 'fontFamily' },
    { variableName, value: fontWeightToSwift(font.weight), type: 'fontWeight' },
  ] as MultipleConversionOutput;
}
