import { SpecifyGradientValue } from '@specifyapp/specify-design-token-format';
import { colorToSwift } from './color.js';

export function gradientToSwift(gradient: SpecifyGradientValue) {
  const stops = gradient.colorStops.flatMap(stop => {
    const color = colorToSwift(stop.color);
    if (!color) return [];

    return `Gradient.Stop(color: ${color}, location: ${stop.position})`;
  });

  return `Gradient(stops: [${stops.join(', ')}])`;
}
