import { SpecifyRadiiValue } from '@specifyapp/specify-design-token-format';
import { radiusToSwift } from './radius.js';
import { MultipleConversionOutput } from '../convertTokens.js';

export function radiiToSwift(radii: SpecifyRadiiValue, variableName: string) {
  return radii.flatMap<MultipleConversionOutput[number]>((v, i) => {
    return [
      {
        value: radiusToSwift(v),
        variableName: `${variableName}${i}`,
        type: 'radius',
      },
    ];
  });
}
