import { SpecifyShadowsValue } from '@specifyapp/specify-design-token-format';
import { shadowToSwift } from './shadow.js';
import { MultipleConversionOutput } from '../convertTokens.js';

export function shadowsToSwift(shadows: SpecifyShadowsValue, variableName: string) {
  return shadows.flatMap<MultipleConversionOutput[number]>((v, i) =>
    shadowToSwift(v, `${variableName}${i}`),
  );
}
