import { SpecifySpacingsValue } from '@specifyapp/specify-design-token-format';
import { numberToDouble } from '../utils.js';
import { MultipleConversionOutput } from '../convertTokens.js';

export function spacingsToSwift(
  value: SpecifySpacingsValue,
  variableName: string,
): MultipleConversionOutput {
  return value.map((v, i) => ({
    variableName: `${variableName}${i}`,
    value: numberToDouble(v.value),
    type: 'spacing',
  }));
}
