import { SpecifyTextStyleValue } from '@specifyapp/specify-design-token-format';

export function textAlignmentHorizontalToSwift(
  textAlign: SpecifyTextStyleValue['textAlignHorizontal'],
) {
  switch (textAlign) {
    case 'end':
    case 'right':
      return 'TextAlignment.trailing';
    case 'initial':
    case 'left':
    case 'start':
      return 'TextAlignment.leading';
    case 'center':
      return 'TextAlignment.center';
    case 'justify':
    case 'justify-all':
    case 'match-parent':
      return undefined;
  }
}
