import { SpecifyGradientsValue } from '@specifyapp/specify-design-token-format';
import { gradientToTailwind } from './gradient.js';

export function gradientsToTailwind(value: SpecifyGradientsValue) {
  return `${value
    .flatMap(v => {
      const converted = gradientToTailwind(v);
      return !!converted ? [converted] : [];
    })
    .join(', ')}`;
}
