import { SpecifyShadowValue } from '@specifyapp/specify-design-token-format';
import { colorToTailwind } from './color.js';
import { dimensionToTailwind } from './dimension.js';

export function shadowToTailwind(value: SpecifyShadowValue) {
  return `${value.type === 'inner' ? 'inset ' : ''}${dimensionToTailwind(
    value.offsetX,
  )} ${dimensionToTailwind(value.offsetY)} ${dimensionToTailwind(value.blurRadius)} ${
    colorToTailwind(value.color) ?? ''
  }`;
}
