import {
  SpecifyFontFamilyValue,
  SpecifyFontWeightValue,
  SpecifyOpacityValue,
  SpecifyZIndexValue,
} from '@specifyapp/specify-design-token-format';

export function simpleValueToTailwind(
  value: SpecifyFontFamilyValue | SpecifyFontWeightValue | SpecifyOpacityValue | SpecifyZIndexValue,
) {
  return typeof value === 'number' ? value.toString() : value;
}
