import {
  SpecifyBorderValue,
  SpecifyColorValue,
  SpecifyShadowValue,
  SpecifyTextStyleValue,
} from '@specifyapp/specify-design-token-format';
import { colorToTarget } from '../colorToTarget.js';

export function getCompositeUpdateValue(
  value: SpecifyTextStyleValue | SpecifyBorderValue | SpecifyShadowValue,
  target: SpecifyColorValue['model'],
) {
  const { color } = value;

  if (!color) return;

  return { color: colorToTarget(color, target) };
}
