import { SpecifyColorValue, SpecifyGradientValue } from '@specifyapp/specify-design-token-format';
import { colorToTarget } from '../colorToTarget.js';

export function getGradientUpdateValue(
  gradient: SpecifyGradientValue,
  target: SpecifyColorValue['model'],
) {
  return {
    colorStops: gradient.colorStops.map(({ color }) => ({ color: colorToTarget(color, target) })),
  };
}
