import { SpecifyColorValue, SpecifyGradientsValue } from '@specifyapp/specify-design-token-format';
import { getGradientUpdateValue } from './gradient.js';

export function getGradientsUpdateValue(
  gradients: SpecifyGradientsValue,
  target: SpecifyColorValue['model'],
) {
  return gradients.map(gradient => getGradientUpdateValue(gradient, target));
}
