import { SpecifyColorValue, SpecifyShadowsValue } from '@specifyapp/specify-design-token-format';
import { getCompositeUpdateValue } from './composite.js';

export function getShadowsUpdateValue(
  shadows: SpecifyShadowsValue,
  target: SpecifyColorValue['model'],
) {
  return shadows.map(shadow => getCompositeUpdateValue(shadow, target));
}
