import { SpecifyCubicBezierValue } from '@specifyapp/specify-design-token-format';
import { NumberRounder } from '../makeNumberRounder.js';

export function getCubicBezierUpdateValue(
  cubicBezier: SpecifyCubicBezierValue,
  roundNumber: NumberRounder,
) {
  return cubicBezier.map(v => roundNumber(v));
}
